// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

contract Counter {
    uint8 public myNumber = 255;

    function add() public {
        // 在 Solidity 0.8+ 中，这行代码会失败并回滚交易
        // 因为 255 + 1 会导致 uint8 上溢
        myNumber = myNumber + 1; 
    }
